import React, {Fragment, useState} from 'react';
import Adminsidebar from "../../../components/Adminsidebar";
import AdminTopnav from "../../../components/AdminTopnav";
import Adminfooter from "../../../components/Adminfooter";
import {
    Dropdown,
    Menu,
    PageHeader,
    message,
    Card,
    Row,
    Col,
    Button,
    Table,
    Tag,
    Space,
    notification,
    Modal, DatePicker
} from "antd";
import {
    AppstoreOutlined,
    DeleteOutlined,
    DownOutlined,
    EditOutlined,
    EllipsisOutlined,
    MenuOutlined, PlusOutlined
} from "@ant-design/icons";
import Link from "react-router-dom/es/Link";
import Search from "antd/es/input/Search";
import ImgCrop from "antd-img-crop";
import Upload from "antd/es/upload/Upload";


function DataGuruAdmin() {
    const [grid, setGrid] = useState(false);
    const [isModalVisible, setIsModalVisible] = useState(false);
    const [fileList, setFileList] = useState([
        {
            uid: '-1',
            name: 'image.png',
            status: 'done',
            url: 'https://zos.alipayobjects.com/rmsportal/jkjgkEfvpUPVyRjUImniVslZfWPnJuuZ.png',
        },
    ]);
    const channelList = [
        {
            imageUrl: 'user.png',
            namaGuru: 'Nining Hanjarwati',
            nomorSk: 'SKI-MEI-012212',
            tahunAktif: '2010',
            tag1: 'Kelas 5 A',
            tag2: 'Kelas 4 B',
            tag3: 'Kelas 3 C',
            statusGuru: ['pns'],
            status: 'Pegawai Negeri Sipil'
        },
        {
            imageUrl: 'user.png',
            namaGuru: 'Eko Prasetyo',
            nomorSk: 'SKI-MEI-012212',
            tahunAktif: '2005',
            tag1: '',
            tag2: 'Kelas 5 A',
            tag3: '2 A',
            statusGuru: ['pns'],
            status: 'Pegawai Negeri Sipil'

        },
        {
            imageUrl: 'user.png',
            namaGuru: 'Bambang Setiawan',
            nomorSk: 'SKI-MEI-012212',
            tahunAktif: '2011',
            tag1: 'Kelas 5 A',
            tag2: 'Kelas 4 B',
            tag3: 'Kelas 3 C',
            statusGuru: ['pns'],
            status: 'Pegawai Negeri Sipil'
        },
        {
            imageUrl: 'user.png',
            namaGuru: 'Triyanto',
            nomorSk: 'SKI-MEI-012212',
            tahunAktif: '2009',
            tag1: 'Kelas 5 A',
            tag2: 'Kelas 4 B',
            tag3: 'Kelas 3 C',
            statusGuru: ['pns'],
            status: 'Pegawai Negeri Sipil'
        },
        {
            imageUrl: 'user.png',
            namaGuru: 'Nunung Sutisna',
            nomorSk: 'SKI-MEI-012212',
            tahunAktif: '2010',
            tag1: 'Kelas 5 A',
            tag2: 'Kelas 4 B',
            tag3: 'Kelas 3 C',
            statusGuru: ['pns'],
            status: 'Pegawai Negeri Sipil'
        },
        {
            imageUrl: 'user.png',
            namaGuru: 'Reza Tanuwijaya',
            nomorSk: 'SKI-MEI-012212',
            tahunAktif: '2021',
            tag1: 'Kelas 5 A',
            tag2: 'Kelas 4 B',
            tag3: 'Kelas 3 C',
            statusGuru: ['honorer'],
            status: 'Pegawai Honorer'
        },
    ];

    const columns = [
        {
            title: 'Name',
            dataIndex: 'namaGuru',
            filters: [
                {
                    text: 'John',
                    value: 'John',
                },
                {
                    text: 'James',
                    value: 'James',
                },
            ],
            onFilter: (value, record) => record.namaGuru.indexOf(value) === 0,
            sorter: (a, b) => a.namaGuru.length - b.namaGuru.length,
            sortDirections: ['descend'],
        },
        {
            title: 'Tanggal Lahir',
            dataIndex: 'nomorSk',
            defaultSortOrder: 'descend',
            // sorter: (a, b) => a.nomorSk - b.nomorSk,
        },
        {
            title: 'Status',
            dataIndex: 'statusGuru',
            render: statusGuru => (
                <>
                    {statusGuru.map(statusGuru => {
                        let color = statusGuru.length > 5 ? 'blue' : 'green';
                        return (
                            <Tag style={{borderRadius: '15px'}} color={color} key={statusGuru}>
                                {statusGuru.toUpperCase()}
                            </Tag>

                        );
                    })}
                </>
            ),
            filters: [
                {
                    text: 'Pegawai Negeri Sipil',
                    value: 'pns',
                },
                {
                    text: 'Pegawai Honorer',
                    value: 'honorer',
                },
            ],
            onFilter: (value, record) => record.status.indexOf(value) === 0,
        },
        {
            title: 'Tahun Aktif',
            dataIndex: 'tahunAktif',
            defaultSortOrder: 'descend',
            // sorter: (a, b) => a.nomorSk - b.nomorSk,
        },
        {
            title: 'Aksi',
            key: 'action',
            render: (text, record) => (
                <Space size="middle">
                    <EditOutlined style={{color: "blue"}} onClick={() => notification.open({
                        message: 'Edit',
                        description:
                            'Edit user bernama ' + record.namaGuru,
                        duration: 2

                    })}/>
                    <DeleteOutlined style={{color: 'red'}} onClick={() => notification.open({
                        message: 'Delete',
                        description:
                            'Hapus user bernama ' + record.namaGuru,
                        duration: 2
                    })}/>
                </Space>
            ),
        },
    ];

    const _onSelectMenu = ({key}) => {
        message.info(`Click on item ${key}`);
    };

    const _Account = (
        <Menu onClick={_onSelectMenu}>
            <Menu.Item key="1">Ubah</Menu.Item>
            <Menu.Item key="2">Hapus</Menu.Item>
        </Menu>
    );

    const _filterMenu = (
        <Menu onClick={_onSelectMenu}>
            <Menu.Item key="1">1st filter</Menu.Item>
            <Menu.Item key="2">2nd filter</Menu.Item>
            <Menu.Item key="3">3rd filter</Menu.Item>
        </Menu>
    );

    const _sortMenu = (
        <Menu onClick={_onSelectMenu}>
            <Menu.Item key="1">1st sort</Menu.Item>
            <Menu.Item key="2">2nd sort</Menu.Item>
            <Menu.Item key="3">3rd sort</Menu.Item>
        </Menu>
    );

    const _onSearch = value => console.log(value);

    function onChangeTable(pagination, filters, sorter, extra) {
        console.log('params', pagination, filters, sorter, extra);
    }

    const showModal = () => {
        setIsModalVisible(true);
    };

    const handleOk = () => {
        setIsModalVisible(false);
    };

    const handleCancel = () => {
        setIsModalVisible(false);
    };

    const CardDataGuru = () => (
        <div className="middle-sidebar-left">
            <div className="row">
                {channelList.map((value, index) => (
                    <div className="col-xl-4 col-lg-6 col-md-6" key={index}>
                        <div
                            className="card mb-4 d-block w-100 shadow-md rounded-lg p-xxl-5 p-4 border-0 text-center">
                                                <span
                                                    className="badge badge-success rounded-xl position-absolute px-2 py-1 left-0 ml-4 top-0 mt-3">
                                                    Aktif
                                                </span>
                            <Dropdown className='position-absolute right-0 mr-4 top-0 mt-3'
                                      overlay={_Account}>
                                <EllipsisOutlined/>
                            </Dropdown>
                            {/*<a*/}
                            {/*    href="/default-channel"*/}
                            {/*    className="position-absolute right-0 mr-4 top-0 mt-3"*/}
                            {/*>*/}
                            {/*    <i className="ti-more text-grey-500 font-xs"></i>*/}
                            {/*</a>*/}

                            <a
                                href="/default-channel"
                                className="btn-round-xxxl rounded-lg bg-lightblue ml-auto mr-auto"
                            >
                                <img
                                    src={`assets/images/${value.imageUrl}`}
                                    alt="icon"
                                    className="p-1 w-100"
                                />
                            </a>
                            <h4 className="fw-700 font-xs mt-3 mb-3">{value.title}</h4>
                            <div className="clearfix"></div>
                            {value.tag1 ? (
                                <span
                                    className="font-xsssss fw-700 pl-3 pr-3 lh-32 text-uppercase rounded-xxl ls-2 alert-success d-inline-block text-success mb-1 mr-1">
                            {value.tag1}
                          </span>
                            ) : (
                                ''
                            )}
                            {value.tag2 ? (
                                <span
                                    className="font-xsssss fw-700 pl-3 pr-3 lh-32 text-uppercase rounded-xxl ls-2 bg-lightblue d-inline-block text-grey-800 mb-1 mr-1">
                            {value.tag2}
                          </span>
                            ) : (
                                ''
                            )}
                            {value.tag3 ? (
                                <span
                                    className="font-xsssss fw-700 pl-3 pr-3 lh-32 text-uppercase rounded-xxl ls-2 alert-info d-inline-block text-info mb-1">
                            {value.tag3}
                          </span>
                            ) : (
                                ''
                            )}
                            <div className="clearfix"></div>
                            <div className="mt-4 mx-auto">
                                <div className="row ml-3">
                                    <div className="col-4">
                                        <p className="font-xssss float-left lh-1">Status</p>
                                    </div>
                                    <div className="col-8">
                                        <p className="font-xssss float-left lh-1">: {value.status}</p>
                                    </div>
                                </div>

                                <div className="row ml-3">
                                    <div className="col-4">
                                        <p className="font-xssss float-left lh-1">Tahun Aktif</p>
                                    </div>
                                    <div className="col-8">
                                        <p className="font-xssss float-left lh-1">: {value.tahunAktif}</p>
                                    </div>
                                </div>

                                <div className="row ml-3">
                                    <div className="col-4">
                                        <p className="font-xssss float-left lh-1">Nomor SK</p>
                                    </div>
                                    <div className="col-8">
                                        <p className="font-xssss float-left lh-1">: {value.nomorSk}</p>
                                    </div>
                                </div>
                            </div>
                        </div>
                    </div>
                ))}
            </div>
        </div>
    )

    const TableDataGuru = () => (
        <Table className="mt-4"
               columns={columns}
               dataSource={channelList}
               onChange={onChangeTable}
               pagination={{position: ['bottomCenter']}}
               rowClassName="bg-greylight text-grey-900"
        />
    )


    const onChange = ({fileList: newFileList}) => {
        setFileList(newFileList);
    };

    const onPreview = async file => {
        let src = file.url;
        if (!src) {
            src = await new Promise(resolve => {
                const reader = new FileReader();
                reader.readAsDataURL(file.originFileObj);
                reader.onload = () => resolve(reader.result);
            });
        }
        const image = new Image();
        image.src = src;
        const imgWindow = window.open(src);
        imgWindow.document.write(image.outerHTML);
    };

    return (
        <Fragment>
            <div id="wrapper">
                <Adminsidebar/>

                <div id="content-wrapper" className="d-flex flex-column">
                    <div id="content">
                        <AdminTopnav/>
                        <div className="container px-3 py-4">
                            <Modal title="Tambah Data Guru" visible={isModalVisible} onOk={handleOk} onCancel={handleCancel}>
                                <div className="row mb-3">
                                    <div className="col-lg-12">
                                        <div className="d-flex justify-content-center">
                                            <Card className="bg-lightblue" style={{width: 157}}>
                                                <ImgCrop rotate>
                                                    <Upload
                                                        className="avatar-uploader"
                                                        action="https://www.mocky.io/v2/5cc8019d300000980a055e76"
                                                        listType="picture-card"
                                                        fileList={fileList}
                                                        onChange={onChange}
                                                        onPreview={onPreview}
                                                    >
                                                        {fileList.length < 1 && <PlusOutlined/>}
                                                    </Upload>
                                                </ImgCrop>
                                            </Card>
                                        </div>
                                    </div>
                                </div>
                                <form className="px-3 py-4" action="#">
                                    <div className="row">
                                        <div className="col-lg-6 mb-3">
                                            <div className="form-group">
                                                <label className="mont-font fw-600 font-xsss">
                                                    Name
                                                </label>
                                                <input
                                                    defaultValue="Aneta"
                                                    name="nama_sekolah"
                                                    type="text"
                                                    className="form-control"
                                                />
                                            </div>
                                        </div>

                                        <div className="col-lg-6 mb-3">
                                            <div className="form-group">
                                                <label className="mont-font fw-600 font-xsss">
                                                    Provinsi
                                                </label>
                                                <input
                                                    defaultValue="Jakarta"
                                                    name="provinsi"
                                                    type="text"
                                                    className="form-control"
                                                />
                                            </div>
                                        </div>
                                    </div>
                                    <div className="row">
                                        <div className="col-lg-6 mb-3">
                                            <div className="form-group">
                                                <label className="mont-font fw-600 font-xsss">
                                                    Phone
                                                </label>
                                                <input
                                                    type="number"
                                                    defaultValue="08328382832"
                                                    name="phone"
                                                    className="form-control"
                                                />
                                            </div>
                                        </div>

                                        <div className="col-lg-6 mb-3">
                                            <div className="form-group">
                                                <label className="mont-font fw-600 font-xsss">
                                                    Kota / Kabupaten
                                                </label>
                                                <input
                                                    type="text"
                                                    defaultValue="Jakarta Utara"
                                                    name="kota"
                                                    className="form-control"
                                                />
                                            </div>
                                        </div>
                                    </div>
                                    <div className="row">
                                        <div className="col-lg-6 mb-3">
                                            <div className="form-group">
                                                <label className="mont-font fw-600 font-xsss">
                                                    Fax
                                                </label>
                                                <input
                                                    type="number"
                                                    defaultValue="28302382"
                                                    name="fax"
                                                    className="form-control"
                                                />
                                            </div>
                                        </div>

                                        <div className="col-lg-6 mb-3">
                                            <div className="form-group">
                                                <label className="mont-font fw-600 font-xsss">
                                                    Kecamatan
                                                </label>
                                                <input
                                                    type="text"
                                                    defaultValue="Kelapa Gading Timur"
                                                    name="kecamatan"
                                                    className="form-control"
                                                />
                                            </div>
                                        </div>
                                    </div>
                                    <div className="row">
                                        <div className="col-lg-6 mb-3">
                                            <div className="form-group">
                                                <label className="mont-font fw-600 font-xsss">
                                                    Address
                                                </label>
                                                <input
                                                    type="text"
                                                    defaultValue="Wisma Permata"
                                                    name="address"
                                                    className="form-control"
                                                />
                                            </div>
                                        </div>

                                        <div className="col-lg-6 mb-3">
                                            <div className="form-group">
                                                <label className="mont-font fw-600 font-xsss">
                                                    Kelurahan
                                                </label>
                                                <input
                                                    type="text"
                                                    defaultValue="Kelapa Gading"
                                                    name="kelurahan"
                                                    className="form-control"
                                                />
                                            </div>
                                        </div>
                                    </div>
                                    <div className="row">
                                        <div className="col-lg-6 mb-3">
                                            <div className="form-group">
                                                <label className="mont-font fw-600 font-xsss">
                                                    Website
                                                </label>
                                                <input
                                                    type="text"
                                                    defaultValue="aneta.id"
                                                    name="website"
                                                    className="form-control"
                                                />
                                            </div>
                                        </div>

                                        <div className="col-lg-6 mb-3">
                                            <div className="form-group">
                                                <label className="mont-font fw-600 font-xsss">
                                                    Tahun Berdiri
                                                </label>
                                                <DatePicker
                                                    className="form-control"
                                                    picker="year"
                                                    placeholder="Pilih Tahun"
                                                    name="tahun_berdiri"
                                                />
                                            </div>
                                        </div>
                                    </div>
                                </form>
                            </Modal>

                            <div className="row mb-3">
                                <div className="col-lg-12">
                                    <PageHeader
                                        className="site-page-header card bg-lightblue text-grey-900 fw-700 "
                                        onBack={() => window.history.back()}
                                        title="Data Guru / Tenaga Pengajar"
                                    />
                                </div>
                            </div>
                            <Card className="card bg-lightblue border-0 mb-4 text-grey-900">
                                <Row>
                                    <Col span={12}>
                                            <Button className="mr-4" type="primary" shape="round" size='middle' onClick={showModal}>
                                                Tambah Data
                                            </Button>
                                        <Dropdown overlay={_filterMenu}>
                                            <a className="ant-dropdown-link mr-4 font-bold" onClick={e => e.preventDefault()}>
                                                Filter by <DownOutlined/>
                                            </a>
                                        </Dropdown>
                                        <Dropdown overlay={_sortMenu}>
                                            <a className="ant-dropdown-link font-bold" onClick={e => e.preventDefault()}>
                                                Sort by <DownOutlined/>
                                            </a>
                                        </Dropdown>
                                    </Col>
                                    <Col span={12}>
                                        <div className="float-right">
                                            <Search className="mr-5" placeholder="Cari kata kunci" allowClear
                                                    onSearch={_onSearch} style={{width: 250, lineHeight: '20px'}}/>
                                            {grid == false ?
                                                <a>
                                                    <AppstoreOutlined style={{fontSize: '30px'}}
                                                                      onClick={() => setGrid(true)}/>
                                                </a> :
                                                <a>
                                                    <MenuOutlined style={{fontSize: '30px'}} onClick={() => setGrid(false)}/>
                                                </a>}
                                        </div>
                                    </Col>
                                </Row>
                            </Card>
                            {grid ? <CardDataGuru/> : <TableDataGuru/>}
                        </div>


                        <Adminfooter/>
                    </div>
                </div>
            </div>
        </Fragment>
    );
}

export default DataGuruAdmin;
