import react, { Fragment, useState } from "react"

import { Menu, Card, Row, Col, Button, Dropdown, message, Calendar, Table, Space, notification } from "antd";
import { DownOutlined,
         AppstoreOutlined,
         MenuOutlined,
         DeleteOutlined,
         EditOutlined,
         CalendarOutlined,
         TableOutlined
} from "@ant-design/icons";
import Search from "antd/lib/input/Search";

import { Link, useHistory} from 'react-router-dom';

import Adminsidebar from '../../../components/Adminsidebar';
import AdminTopnav from '../../../components/AdminTopnav';
import Adminfooter from '../../../components/Adminfooter';

export default function JadwalPelajaranAdminDetail() {
    const [calendar, setCalendar] = useState(true)

    // const _onSelectMenu = ({key}) => {
    //     message.info(`Click on item ${key}`);
    // };

    // const _filterMenu = (
    //     <Menu onClick={_onSelectMenu}>
    //         <Menu.Item key="1">1st filter</Menu.Item>
    //         <Menu.Item key="2">2nd filter</Menu.Item>
    //         <Menu.Item key="3">3rd filter</Menu.Item>
    //     </Menu>
    // );

    // const _sortMenu = (
    //     <Menu onClick={_onSelectMenu}>
    //         <Menu.Item key="1">1st sort</Menu.Item>
    //         <Menu.Item key="2">2nd sort</Menu.Item>
    //         <Menu.Item key="3">3rd sort</Menu.Item>
    //     </Menu>
    // );

    const CalendarData = () => {
        return(
            <Calendar className="mx-4"/>
        );
    }

    const TableData = () => {
        const _onSelectMenu = ({key}) => {
            message.info(`Click on item ${key}`);
        };

        const columns = [
            {
                title: 'Mata Pelajaran',
                dataIndex: 'namaPelajaran',
                align: 'center',
                filters: [
                    {
                        text: 'Tematik 1',
                        value: 'Tematik 1',
                    },
                    {
                        text: 'Tematik 2',
                        value: 'Tematik 2',
                    },
                ],
                // onFilter: (value, record) => record.namaPelajaran.indexOf(value) === 0,
                // sorter: (a, b) => a.namaPelajaran.length - b.namaPelajaran.length,
            },
            {
                title: 'Guru/Tenaga Pengajar',
                dataIndex: 'namaPengajar',
                align: 'center',
                filters: [
                    {
                        text: 'Sri Wahyuni S.pd',
                        value: 'Sri Wahyuni S.pd',
                    },
                    {
                        text: 'Siti Mulyani S.pd',
                        value: 'Siti Mulyani S.pd',
                    },
                ],
                // onFilter: (value, record) => record.namaPengajar.indexOf(value) === 0,
                // sorter: (a, b) => a.namaPengajar.length - b.namaPengajar.length,
            },
            {
                title: 'Waktu Mulai - Waktu Selesai',
                dataIndex: 'waktu',
                align: 'center'
            },
            {
                title: 'Aksi',
                key: 'action',
                align: 'center',
                render: (text, record) => (
                    <Space size="middle">
                        <EditOutlined style={{color: "blue"}} onClick={() => notification.open({
                            message: 'Edit',
                            description:
                                'Edit user bernama ' + record.namaPelajaran,
                            duration: 2

                        })}/>
                        <DeleteOutlined style={{color: 'red'}} onClick={() => notification.open({
                            message: 'Delete',
                            description:
                                'Hapus user bernama ' + record.namaPelajaran,
                            duration: 2
                        })}/>
                    </Space>
                ),
            },
        ];

        const data = [
            {
                namaPelajaran: 'Tematik 1',
                namaPengajar: 'Sri Wahyuni S.pd',
                waktu: '07.30 - 08.30',
            },
            {
                namaPelajaran: 'Tematik 2',
                namaPengajar: 'Siti Mulyani S.pd',
                waktu: '08.30 - 09.30',
            },
            {
                namaPelajaran: ' ',
                namaPengajar: ' ',
                waktu: ' ',
            },
            {
                namaPelajaran: ' ',
                namaPengajar: ' ',
                waktu: ' ',
            },
        ];

        function onChangeTable(pagination, filters, sorter, extra) {
            console.log('params', pagination, filters, sorter, extra);
        }

        return (
            <>
                <div className="px-4 mt-2">
                    <div className="bg-grey">
                      <p className="strong text-black pl-4 mb-0">SENIN</p>
                    </div>
                </div>
                <Table className="mx-4"
                        columns={columns}
                        dataSource={data}
                        onChange={onChangeTable}
                        pagination={{position: ['none']}}
                        rowClassName="bg-greylight text-grey-900"/>
                <div className="px-4 mt-4">
                  <div className="bg-grey">
                    <p className="strong text-black pl-4 mb-0">SELASA</p>
                  </div>
                </div>
                <Table className="mx-4"
                        columns={columns}
                        dataSource={data}
                        onChange={onChangeTable}
                        pagination={{position: ['none']}}
                        rowClassName="bg-greylight text-grey-900"/>
                <div className="px-4 mt-4">
                  <div className="bg-grey">
                    <p className="strong text-black pl-4 mb-0">RABU</p>
                  </div>
                </div>
                <Table className="mx-4"
                        columns={columns}
                        dataSource={data}
                        onChange={onChangeTable}
                        pagination={{position: ['none']}}
                        rowClassName="bg-greylight text-grey-900"/>
            </>
        );
    }

  const history = useHistory();

  const _onSearch = value => console.log(value);

  return (
    <Fragment>
      <div id="wrapper">
        <Adminsidebar />

        <div id="content-wrapper" className="d-flex flex-column">
          <div id="content">
            <AdminTopnav />

            <div className="container px-3 py-4">
              <div className="row">
                <div className="col-lg-12">
                  <div className="flex-wrap pr-4 pl-4">
                    <div className="card-body p-4 w-100 bg-current border-0 d-flex rounded-lg mb-4">
                      <i onClick={history.goBack} className="cursor-pointer d-inline-block mt-2 ti-arrow-left font-sm text-white"></i>
                      <h4 className="font-xs text-white fw-600 ml-4 mb-0 mt-2">
                        Jadwal Pelajaran
                      </h4>
                    </div>
                    <Card className="w-full shadow-md py-0 mb-4">
                      <Row>
                        <Col span={12}>
                          <Link to='/admin-setupjadwalpelajaran'>
                          <Button className="mr-4 bg-current" type="primary" shape="round" size='middle'>
                            Tambah Data
                          </Button>
                          </Link>
                          {/* <Dropdown overlay={_filterMenu}>
                              <a className="ant-dropdown-link mr-4 font-bold" onClick={e => e.preventDefault()}>
                                  Filter by <DownOutlined/>
                              </a>
                          </Dropdown>
                          <Dropdown overlay={_sortMenu}>
                              <a className="ant-dropdown-link font-bold" onClick={e => e.preventDefault()}>
                                  Sort by <DownOutlined/>
                              </a>
                          </Dropdown> */}
                        </Col>
                        <Col span={12}>
                          <div className="float-right">
                            <Search className="mr-6" placeholder="Cari kata kunci" allowClear
                                    onSearch={_onSearch} style={{width: 250, lineHeight: '20px'}}/>
                            <a>
                              <AppstoreOutlined style={{fontSize: '30px', marginRight: '10px'}}/>
                            </a>
                            <a>
                              <MenuOutlined style={{fontSize: '30px'}}/>
                            </a>
                          </div>
                        </Col>
                      </Row>
                    </Card>
                  </div>
                  <div className="px-4 py-2">
                    {calendar == false ?
                    <p  onClick={() => setCalendar(true)}
                        className='align-items-center d-flex justify-content-end cursor-pointer'>
                            <TableOutlined style={{fontSize: '22px'}}/>
                            <p className="font-weight-bolder ml-2 mb-0">Tampilkan Mode Tabel</p>
                    </p> :
                    <p  onClick={() => setCalendar(false)}
                        className='align-items-center d-flex justify-content-end cursor-pointer'>
                            <CalendarOutlined style={{fontSize: '20px'}}/>
                            <p className="font-weight-bolder ml-2 mb-0">Tampilkan Mode Kalender</p>
                    </p>}
                  </div>
                  {calendar ? <TableData/> : <CalendarData/>}
                </div>
              </div>
            </div>
            <Adminfooter />
          </div>
        </div>
      </div>
    </Fragment>
  );
}
