import React, {Fragment, useEffect, useState} from 'react';
import Adminsidebar from "../../../components/Adminsidebar";
import AdminTopnav from "../../../components/AdminTopnav";
import Adminfooter from "../../../components/Adminfooter";
import {
    Dropdown,
    Menu,
    PageHeader,
    message,
    Card,
    Row,
    Col,
    Button,
    Tag,
    Space,
    notification,
    Table,
    Modal, DatePicker, Tabs
} from "antd";
import {
    AppstoreOutlined,
    DeleteOutlined,
    DownOutlined,
    EditOutlined,
    EllipsisOutlined,
    MenuOutlined, PlusOutlined
} from "@ant-design/icons";
import Link from "react-router-dom/es/Link";
import Search from "antd/es/input/Search";
import ImgCrop from "antd-img-crop";
import Upload from "antd/es/upload/Upload";
import axios from "axios";


function DataSiswaAdmin() {
    const [grid, setGrid] = useState(false);
    const [isViewSiswa, setIsViewSiswa] = useState(true);

    useEffect(() => {
        console.log('useEffect');
       axios.post('http://10.1.6.68:31729/apiman-gateway/AnetaProvider/default/1.0?apikey=204824a8-b1a2-4d74-8383-7fdd7716d747', {
           "processDefinitionId": "getdataglobal:5:7248a1b1-d5a7-11ec-a658-66fc627bf211",
           "returnVariables": true,
           "variables": [
               {
                   "name": "global_getdata",
                   "type": "json",
                   "value": {
                       "tbl_name": "m_institutes",
                       "tbl_coloumn": [
                           "*"
                       ],
                       "pagination": true,
                       "total_result": 10
                   }
               }
           ]
       }, {
           headers: {
               'Content-Type': 'application/json',
           },
       }).then(function (response) {
           const data = JSON.parse(response.data.variables[2].value);
           console.log(data);
       });
    }, []);

    const _onSelectMenu = ({key}) => {
        message.info(`Click on item ${key}`);
    };

    const _account = (
        <Menu onClick={_onSelectMenu}>
            <Menu.Item key="1">Ubah</Menu.Item>
            <Menu.Item key="2">Delete</Menu.Item>
        </Menu>
    );

    const _filterMenu = (
        <Menu onClick={_onSelectMenu}>
            <Menu.Item key="1">1st filter</Menu.Item>
            <Menu.Item key="2">2nd filter</Menu.Item>
            <Menu.Item key="3">3rd filter</Menu.Item>
        </Menu>
    );

    const _sortMenu = (
        <Menu onClick={_onSelectMenu}>
            <Menu.Item key="1">1st sort</Menu.Item>
            <Menu.Item key="2">2nd sort</Menu.Item>
            <Menu.Item key="3">3rd sort</Menu.Item>
        </Menu>
    );

    const _onSearch = value => console.log(value);

    const columns = [
        {
            title: 'NIS',
            dataIndex: 'nis',
            defaultSortOrder: 'ascend',
            // sorter: (a, b) => a.nis - b.nis,
        },
        {
            title: 'Name',
            dataIndex: 'namaSiswa',
            filters: [
                {
                    text: 'John',
                    value: 'John',
                },
                {
                    text: 'James',
                    value: 'James',
                },
            ],
            onFilter: (value, record) => record.namaSiswa.indexOf(value) === 0,
            sorter: (a, b) => a.namaSiswa.length - b.namaSiswa.length,
            sortDirections: ['descend'],
        },
        {
            title: 'Tanggal Lahir',
            dataIndex: 'tanggalLahir',
            defaultSortOrder: 'descend',
            // sorter: (a, b) => a.tanggalLahir - b.tanggalLahir,
        },
        {
            title: 'Email',
            dataIndex: 'email',
            defaultSortOrder: 'descend',
        },
        {
            title: 'Status',
            dataIndex: 'status',
            render: status => (
                <>
                    {status.map(status => {
                        let color = status.length > 5 ? 'red' : 'green';
                        return (
                            <Tag style={{borderRadius: '15px'}} color={color} key={status}>
                                {status.toUpperCase()}
                            </Tag>

                        );
                    })}
                </>
            ),
            filters: [
                {
                    text: 'Aktif',
                    value: 'aktif',
                },
                {
                    text: 'Nonaktif',
                    value: 'nonAktif',
                },
            ],
            onFilter: (value, record) => record.status.indexOf(value) === 0,
        },
        {
            title: 'Aksi',
            key: 'action',
            render: (text, record) => (
                <Space size="middle">
                    <EditOutlined style={{color: "blue"}} onClick={() => notification.open({
                        message: 'Edit',
                        description:
                            'Edit user bernama ' + record.namaSiswa,
                        duration: 2

                    })}/>
                    <DeleteOutlined style={{color: 'red'}} onClick={() => notification.open({
                        message: 'Delete',
                        description:
                            'Hapus user bernama ' + record.namaSiswa,
                        duration: 2
                    })}/>
                </Space>
            ),
        },
    ];

    const channelList = [
        {
            nis: '001',
            imageUrl: 'user.png',
            namaSiswa: 'John Brown',
            tanggalLahir: '01/01/2020',
            tag1: '',
            tag2: 'Kelas 5 A',
            tag3: '',
            email: 'johnbrown@email.com',
            status: ['aktif'],
        },
        {
            nis: '002',
            imageUrl: 'user.png',
            namaSiswa: 'John Cena',
            tanggalLahir: '01/01/2020',
            tag1: '',
            tag2: 'Kelas 5 A',
            tag3: '',
            email: 'john@email.com',
            status: ['aktif'],
        },
        {
            nis: '003',
            imageUrl: 'user.png',
            namaSiswa: 'John Wick',
            tanggalLahir: '01/01/2020',
            tag1: '',
            tag2: '',
            tag3: 'Kelas 3 C',
            email: 'johnwick@email.com',
            status: ['aktif'],
        },
        {
            nis: '004',
            imageUrl: 'user.png',
            namaSiswa: 'John Brad',
            tanggalLahir: '01/01/2020',
            tag1: '',
            tag2: '',
            tag3: 'Kelas 3 C',
            email: 'brad@email.com',
            status: ['aktif'],
        },
        {
            nis: '005',
            imageUrl: 'user.png',
            namaSiswa: 'James Chloe',
            tanggalLahir: '01/01/2020',
            tag1: '',
            tag2: 'Kelas 4 B',
            tag3: '',
            email: 'chloe@email.com',
            status: ['aktif'],
        },
        {
            nis: '006',
            imageUrl: 'user.png',
            namaSiswa: 'Yoru Nozaki',
            tanggalLahir: '01/01/2020',
            tag1: '',
            tag2: '',
            tag3: 'Kelas 3 C',
            email: 'yoru@email.com',
            status: ['nonAktif'],
        },
    ];

    const _Account = (
        <Menu onClick={_onSelectMenu}>
            <Menu.Item key="1">Ubah</Menu.Item>
            <Menu.Item key="2">Hapus</Menu.Item>
        </Menu>
    );


    function onChangeTable(pagination, filters, sorter, extra) {
        console.log('params', pagination, filters, sorter, extra);
    }

    const CardDataSiswa = () => (
        <div className="middle-sidebar-left mt-3">
            <div className="row">
                {channelList.map((value, index) => (
                    <div className="col-xl-4 col-lg-6 col-md-6" key={index}>
                        <div
                            className="card mb-4 d-block w-100 shadow-md rounded-lg p-xxl-5 p-4 border-0 text-center">
                                                <span
                                                    className="badge badge-success rounded-xl position-absolute px-2 py-1 left-0 ml-4 top-0 mt-3">
                                                    Aktif
                                                </span>
                            <Dropdown className='position-absolute right-0 mr-4 top-0 mt-3'
                                      overlay={_Account}>
                                <EllipsisOutlined/>
                            </Dropdown>
                            {/*<a*/}
                            {/*    href="/default-channel"*/}
                            {/*    className="position-absolute right-0 mr-4 top-0 mt-3"*/}
                            {/*>*/}
                            {/*    <i className="ti-more text-grey-500 font-xs"></i>*/}
                            {/*</a>*/}

                            <a
                                href="/default-channel"
                                className="btn-round-xxxl rounded-lg bg-lightblue ml-auto mr-auto"
                            >
                                <img
                                    src={`assets/images/${value.imageUrl}`}
                                    alt="icon"
                                    className="p-1 w-100"
                                />
                            </a>
                            <h4 className="fw-700 font-xs mt-3 mb-3">{value.namaSiswa}</h4>
                            <div className="clearfix"></div>
                            {value.tag1 ? (
                                <span
                                    className="font-xsssss fw-700 pl-3 pr-3 lh-32 text-uppercase rounded-xxl ls-2 alert-success d-inline-block text-success mb-1 mr-1">
                            {value.tag1}
                          </span>
                            ) : (
                                ''
                            )}
                            {value.tag2 ? (
                                <span
                                    className="font-xsssss fw-700 pl-3 pr-3 lh-32 text-uppercase rounded-xxl ls-2 bg-lightblue d-inline-block text-grey-800 mb-1 mr-1">
                            {value.tag2}
                          </span>
                            ) : (
                                ''
                            )}
                            {value.tag3 ? (
                                <span
                                    className="font-xsssss fw-700 pl-3 pr-3 lh-32 text-uppercase rounded-xxl ls-2 alert-info d-inline-block text-info mb-1">
                            {value.tag3}
                          </span>
                            ) : (
                                ''
                            )}
                            <div className="clearfix"></div>
                            <div className="mt-4 mx-auto">
                                <div className="row ml-3">
                                    <div className="col-3">
                                        <p className="font-xssss float-left lh-1">NIS</p>
                                    </div>
                                    <div className="col-9">
                                        <p className="font-xssss float-left lh-1">: 001</p>
                                    </div>
                                </div>

                                <div className="row ml-3">
                                    <div className="col-3">
                                        <p className="font-xssss float-left lh-1">TTL</p>
                                    </div>
                                    <div className="col-9">
                                        <p className="font-xssss float-left lh-1">: {value.tanggalLahir}</p>
                                    </div>
                                </div>

                                <div className="row ml-3">
                                    <div className="col-3">
                                        <p className="font-xssss float-left lh-1">Email</p>
                                    </div>
                                    <div className="col-9">
                                        <p className="font-xssss float-left lh-1">: {value.email}</p>
                                    </div>
                                </div>
                            </div>
                        </div>
                    </div>
                ))}
            </div>
        </div>
    )

    const TableDataSiswa = () => (
        <Table className="mt-4"
               columns={columns}
               dataSource={channelList}
               onChange={onChangeTable}
               pagination={{position: ['bottomCenter']}}
               rowClassName="bg-greylight text-grey-900"
        />
    )

    const ViewSiswa = () => {
        return (
            <div className="container px-3 py-4">
                <div className="row mb-3">
                    <div className="col-lg-12">
                        <PageHeader
                            className="site-page-header card bg-lightblue text-grey-900 fw-700 "
                            onBack={() => window.history.back()}
                            title="Data Siswa"
                        />
                    </div>
                </div>
                <Card className="card bg-lightblue border-0 text-grey-900">
                    <Row>
                        <Col span={12}>
                            <Button className="mr-4" type="primary" shape="round" size='middle'
                                    onClick={() => alert('kosong')}>
                                Tambah Data
                            </Button>
                            <Dropdown overlay={_filterMenu}>
                                <a className="ant-dropdown-link mr-4 font-bold"
                                   onClick={e => e.preventDefault()}>
                                    Filter by <DownOutlined/>
                                </a>
                            </Dropdown>
                            <Dropdown overlay={_sortMenu}>
                                <a className="ant-dropdown-link font-bold"
                                   onClick={e => e.preventDefault()}>
                                    Sort by <DownOutlined/>
                                </a>
                            </Dropdown>
                        </Col>
                        <Col span={12}>
                            <div className="float-right">
                                <Search className="mr-5" placeholder="Cari kata kunci" allowClear
                                        onSearch={_onSearch} style={{width: 250, lineHeight: '20px'}}/>
                                {grid == false ?
                                    <a>
                                        <AppstoreOutlined style={{fontSize: '30px'}}
                                                          onClick={() => setGrid(true)}/>
                                    </a> :
                                    <a>
                                        <MenuOutlined style={{fontSize: '30px'}}
                                                      onClick={() => setGrid(false)}/>
                                    </a>}
                            </div>
                        </Col>
                    </Row>
                </Card>

                {grid ? <CardDataSiswa/> : <TableDataSiswa/>}
            </div>
        )
    }

    const TambahSiswa = () => {

    }

    return (
        <Fragment>
            <div id="wrapper">
                <Adminsidebar/>
                <div id="content-wrapper" className="d-flex flex-column">
                    <div id="content">
                        <AdminTopnav/>
                        {isViewSiswa ? <ViewSiswa />: <TambahSiswa />}
                        <Adminfooter/>
                    </div>
                </div>
            </div>
        </Fragment>
    );
}

export default DataSiswaAdmin;
