import React, { Fragment } from 'react';
import {Button,Card, Col, Dropdown, Menu, message, notification, Row, Space, Table} from "antd";
import {
    AppstoreOutlined,
    DeleteOutlined,
    DownOutlined,
    EditOutlined,
    MenuOutlined,
} from "@ant-design/icons";
import Search from "antd/lib/input/Search";

import { Link, useHistory} from 'react-router-dom';

import Adminsidebar from '../../../components/Adminsidebar';
import AdminTopnav from '../../../components/AdminTopnav';
import Adminfooter from '../../../components/Adminfooter';

export default function DataKelasAdmin() {
  // const _onSelectMenu = ({key}) => {
  //   message.info(`Click on item ${key}`);
  // };

  // const _filterMenu = (
  //   <Menu onClick={_onSelectMenu}>
  //       <Menu.Item key="1">1st filter</Menu.Item>
  //       <Menu.Item key="2">2nd filter</Menu.Item>
  //       <Menu.Item key="3">3rd filter</Menu.Item>
  //   </Menu>
  // );

  // const _sortMenu = (
  //   <Menu onClick={_onSelectMenu}>
  //       <Menu.Item key="1">1st sort</Menu.Item>
  //       <Menu.Item key="2">2nd sort</Menu.Item>
  //       <Menu.Item key="3">3rd sort</Menu.Item>
  //   </Menu>
  // );

  const history = useHistory();

  const _onSearch = value => console.log(value);

  const columns = [
    {
        title: 'ID',
        dataIndex: 'id',
        defaultSortOrder: 'ascend',
        // sorter: (a, b) => a.id - b.id,
    },
    {
        title: 'Nama Kelas',
        dataIndex: 'namaKelas',
        filters: [

        ],
        // specify the condition of filtering result
        // here is that finding the name started with `value`
        onFilter: (value, record) => record.namaKelas.indexOf(value) === 0,
        // sorter: (a, b) => a.namaKelas.length - b.namaKelas.length,
        // sortDirections: ['ascend'],
    },
    {
        title: 'Sub Kelas',
        dataIndex: 'subKelas',
        defaultSortOrder: 'ascend',
        // sorter: (a, b) => a.subKelas - b.subKelas,
    },
    {
        title: 'Lokasi Kelas',
        dataIndex: 'lokasiKelas',
        defaultSortOrder: 'ascend',
    },
    {
        title: 'Tahun Akademik',
        dataIndex: 'tahunAkademik',
        defaultSortOrder: 'ascend',
    },
    {
        title: 'Aksi',
        key: 'action',
        render: (text, record) => (
            <Space size="middle">
                <EditOutlined style={{color: "blue"}} onClick={() => notification.open({
                    message: 'Edit',
                    description:
                        'Edit user bernama ' + record.namaKelas,
                    duration: 2

                })}/>
                <DeleteOutlined style={{color: 'red'}} onClick={() => notification.open({
                    message: 'Delete',
                    description:
                        'Hapus user bernama ' + record.namaKelas,
                    duration: 2
                })}/>
            </Space>
        ),
    },
  ];

  const data_sampel = [
    {
        id: '001',
        namaKelas: 'Kelas 2',
        subKelas: 'C',
        lokasiKelas: 'Ruang 404',
        tahunAkademik: '2020/2021',
    },
    {
        id: '002',
        namaKelas: 'Kelas 3',
        subKelas: 'A',
        lokasiKelas: 'Ruang 405',
        tahunAkademik: '2020/2021',
    },
    {
        id: '003',
        namaKelas: 'Kelas 4',
        subKelas: 'B',
        lokasiKelas: 'Ruang 406',
        tahunAkademik: '2020/2021',
    },
    {
        id: '004',
        namaKelas: 'Kelas 5',
        subKelas: 'C',
        lokasiKelas: 'Ruang 407',
        tahunAkademik: '2020/2021',
    },
    {
        id: '005',
        namaKelas: 'Kelas 6',
        subKelas: 'A',
        lokasiKelas: 'Ruang 408',
        tahunAkademik: '2020/2021',
    }
  ]

  function onChangeTable(pagination, filters, sorter, extra) {
    console.log('params', pagination, filters, sorter, extra);
  }

  return (
    <Fragment>
      <div id="wrapper">
        <Adminsidebar />

        <div id="content-wrapper" className="d-flex flex-column">
          <div id="content">
            <AdminTopnav />

            <div className="container px-3 py-4">
              <div className="row">
                <div className="col-lg-12">
                  <div className="flex-wrap pr-4 pl-4">
                    <div className="card-body p-4 w-100 bg-current border-0 d-flex rounded-lg mb-4">
                      <i onClick={history.goBack} className="cursor-pointer d-inline-block mt-2 ti-arrow-left font-sm text-white"></i>
                      <h4 className="font-xs text-white fw-600 ml-4 mb-0 mt-2">
                        Data Kelas
                      </h4>
                    </div>
                    <Card className="shadow-md mb-4">
                      <Row>
                        <Col span={12}>
                          <Link to='/admin-setupkelas'>
                            <Button className="bg-current" type="primary" shape="round" size='middle'>
                              Tambah Data
                            </Button>
                          </Link>
                          {/* <Dropdown overlay={_filterMenu}>
                              <a className="ant-dropdown-link mr-4 font-bold" onClick={e => e.preventDefault()}>
                                  Filter by <DownOutlined/>
                              </a>
                          </Dropdown>
                          <Dropdown overlay={_sortMenu}>
                              <a className="ant-dropdown-link font-bold" onClick={e => e.preventDefault()}>
                                  Sort by <DownOutlined/>
                              </a>
                          </Dropdown> */}
                        </Col>
                        <Col span={12}>
                          <div className="float-right">
                            <Search className="mr-6" placeholder="Cari kata kunci" allowClear
                                    onSearch={_onSearch} style={{width: 250, lineHeight: '20px'}}/>
                            <a>
                              <AppstoreOutlined style={{fontSize: '30px', marginRight: '10px'}}/>
                            </a>
                            <a>
                              <MenuOutlined style={{fontSize: '30px'}}/>
                            </a>
                          </div>
                        </Col>
                      </Row>
                    </Card>
                  </div>
                  <Table className="mx-4 py-8"
                    columns={columns}
                    dataSource={data_sampel}
                    onChange={onChangeTable}
                    pagination={{position: ['bottomCenter']}}
                    rowClassName="bg-greylight text-grey-900"/>
                </div>
              </div>
            </div>
            <Adminfooter />
          </div>
        </div>
      </div>
    </Fragment>
  );
}
