import React, { Fragment, useState } from 'react';
import {Button,Card, Col, Dropdown, Menu, message, notification, Row, Space, Upload} from "antd";
import {
    AppstoreOutlined,
    DeleteOutlined,
    DownOutlined,
    EditOutlined,
    MenuOutlined,
    LoadingOutlined,
    PlusOutlined
} from "@ant-design/icons";
import Search from "antd/lib/input/Search";

import { Link, useHistory} from 'react-router-dom';

import Adminsidebar from '../../../components/Adminsidebar';
import AdminTopnav from '../../../components/AdminTopnav';
import Adminfooter from '../../../components/Adminfooter';

export default function SetupPelajaranAdmin() {
  // const _onSelectMenu = ({key}) => {
  //   message.info(`Click on item ${key}`);
  // };

  // const _filterMenu = (
  //   <Menu onClick={_onSelectMenu}>
  //       <Menu.Item key="1">1st filter</Menu.Item>
  //       <Menu.Item key="2">2nd filter</Menu.Item>
  //       <Menu.Item key="3">3rd filter</Menu.Item>
  //   </Menu>
  // );

  // const _sortMenu = (
  //   <Menu onClick={_onSelectMenu}>
  //       <Menu.Item key="1">1st sort</Menu.Item>
  //       <Menu.Item key="2">2nd sort</Menu.Item>
  //       <Menu.Item key="3">3rd sort</Menu.Item>
  //   </Menu>
  // );

  const [loading, setLoading] = useState(false)
    const [imageUrl, setImageUrl] = useState(null)

    const getBase64 = (img, callback) => {
        const reader = new FileReader();
        reader.addEventListener('load', () => callback(reader.result));
        reader.readAsDataURL(img);
    }

    const beforeUpload = (file) => {
        const isJpgOrPng = file.type === 'image/jpeg' || file.type === 'image/png';
        if (!isJpgOrPng) {
            message.error('You can only upload JPG/PNG file!');
        }
        const isLt2M = file.size / 1024 / 1024 < 2;
        if (!isLt2M) {
            message.error('Image must smaller than 2MB!');
        }
        return isJpgOrPng && isLt2M;
    }

    const _handleChangeImage = info => {
        if (info.file.status === 'uploading') {
            setLoading(true)
            return;
        }
        if (info.file.status === 'done') {
            // Get this url from response in real world.
            getBase64(info.file.originFileObj, imageUrl =>
                setLoading(false)
            );
        }
    };

    const uploadButton = (
        <div>
            {loading ? <LoadingOutlined/> : <PlusOutlined/>}
            <div style={{marginTop: 8}}>Upload</div>
        </div>
    );

    const onChangeDate = (date, dateString) => {
        console.log(date, dateString)
    }

    const _submitProfile = (e) => {
        e.preventDefault();
        console.log('submitted')
        message.success('Profil berhasil diubah')
    }

  const history = useHistory();

  const _onSearch = value => console.log(value);

  return (
    <Fragment>
      <div id="wrapper">
        <Adminsidebar />

        <div id="content-wrapper" className="d-flex flex-column">
          <div id="content">
            <AdminTopnav />

            <div className="container px-3 py-4">
              <div className="row">
                <div className="col-lg-12">
                  <div className="middle-wrap">
                  <div className="card w-100 border-0 bg-white shadow-xs p-0 mb-4">
                    <div className="card-body p-4 w-100 bg-current border-0 d-flex rounded-lg">
                    <i onClick={history.goBack} className="cursor-pointer d-inline-block mt-2 ti-arrow-left font-sm text-white"></i>
                      <h4 className="font-xs text-white fw-600 ml-4 mb-0 mt-2">
                        Tambah Pelajaran
                      </h4>
                    </div>
                    <div className="card-body p-lg-5 p-4 w-100 border-0 ">
                      <div className="row justify-content-center">
                        <div className="col-lg-4 text-center">
                          <figure className="avatar mr-auto mb-4 mt-2 w100">
                            <Card style={{width: 200}}>
                                <Upload
                                    name="avatar"
                                    listType="picture-card"
                                    className="avatar-uploader flex flex-wrap justify-center"
                                    showUploadList={false}
                                    action="https://www.mocky.io/v2/5cc8019d300000980a055e76"
                                    beforeUpload={beforeUpload}
                                    onChange={_handleChangeImage}
                                >
                                    {imageUrl ? <img src={imageUrl} alt="avatar" style={{width: '100%'}}/> : uploadButton}
                                </Upload>
                            </Card>
                          </figure>
                        </div>
                      </div>

                      <form action="#">
                        <div className="row">
                          <div className="col-lg-6 mb-3">
                            <div className="form-group">
                              <label className="mont-font fw-600 font-xsss">
                                Nama Pelajaran
                              </label>
                              <input type="text" className="form-control" />
                            </div>
                          </div>

                          <div className="col-lg-6 mb-3">
                            <div className="form-group">
                              <label className="mont-font fw-600 font-xsss">
                                Kode
                              </label>
                              <input type="text" className="form-control" />
                            </div>
                          </div>
                        </div>

                        <div className="row">
                          <div className="col-lg-6 mb-3">
                            <div className="form-group">
                              <label className="mont-font fw-600 font-xsss">
                                Kelas
                              </label>
                              <input type="text" className="form-control" />
                            </div>
                          </div>

                          <div className="col-lg-6 mb-3">
                            <div className="form-group">
                              <label className="mont-font fw-600 font-xsss">
                                Tahun Akademik
                              </label>
                              <input type="text" className="form-control" />
                            </div>
                          </div>
                        </div>

                        <div className="row">
                          <div className="col-lg-6 mb-3">
                            <div className="form-group">
                              <label className="mont-font fw-600 font-xsss">
                                Max Siswa
                              </label>
                              <input type="text" className="form-control" />
                            </div>
                          </div>

                          <div className="col-lg-12">
                            <Link
                              to="/account-information"
                              className="bg-current text-center text-white font-xsss fw-600 p-3 w175 rounded-lg d-inline-block"
                            >
                              Save
                            </Link>
                            <Link
                              to="/account-information"
                              className="ml-2 bg-lightblue text-center text-blue font-xsss fw-600 p-3 w175 rounded-lg d-inline-block"
                            >
                              Batal
                            </Link>
                          </div>
                        </div>
                      </form>
                    </div>
                  </div>
                </div>
                </div>
              </div>
            </div>
            <Adminfooter />
          </div>
        </div>
      </div>
    </Fragment>
  );
}
