import React, {Fragment, useEffect, useState} from 'react';
import Adminfooter from "../../components/Adminfooter";
import Navheader from "../../components/Navheader";
import Appheader from "../../components/Appheader";
import {
    Dropdown,
    Menu,
    PageHeader,
    message,
    Card,
    Row,
    Col,
    Button,
    Table,
    Tag,
    Space,
    notification,
} from "antd";
import {
    AppstoreOutlined,
    DeleteOutlined,
    DownOutlined,
    EditOutlined,
    EllipsisOutlined,
    MenuOutlined, PlusOutlined
} from "@ant-design/icons";
import Link from "react-router-dom/es/Link";
import Search from "antd/es/input/Search";
import ImgCrop from "antd-img-crop";
import Upload from "antd/es/upload/Upload";
import axios from "axios";
import {BASE_URL} from "../../api/Url";


function DataGuruAdmin() {
    const [grid, setGrid] = useState(false);
    const [isViewGuru, setIsViewGuru] = useState(true);
    const [fileList, setFileList] = useState([
        {
            uid: '-1',
            name: 'image.png',
            status: 'done',
            url: 'https://zos.alipayobjects.com/rmsportal/jkjgkEfvpUPVyRjUImniVslZfWPnJuuZ.png',
        },
    ]);
    const [dataProv, setDataProv] = useState([]);
    const [dataCity, setDataCity] = useState([]);

    const onChange = ({fileList: newFileList}) => {
        setFileList(newFileList);
    };

    const onPreview = async file => {
        let src = file.url;
        if (!src) {
            src = await new Promise(resolve => {
                const reader = new FileReader();
                reader.readAsDataURL(file.originFileObj);
                reader.onload = () => resolve(reader.result);
            });
        }
        const image = new Image();
        image.src = src;
        const imgWindow = window.open(src);
        imgWindow.document.write(image.outerHTML);
    };

    const channelList = [
        {
            imageUrl: 'user.png',
            namaGuru: 'Nining Hanjarwati',
            nomorSk: 'SKI-MEI-012212',
            tahunAktif: '2010',
            tag1: 'Kelas 5 A',
            tag2: 'Kelas 4 B',
            tag3: 'Kelas 3 C',
            statusGuru: ['pns'],
            status: 'Pegawai Negeri Sipil'
        },
        {
            imageUrl: 'user.png',
            namaGuru: 'Eko Prasetyo',
            nomorSk: 'SKI-MEI-012212',
            tahunAktif: '2005',
            tag1: '',
            tag2: 'Kelas 5 A',
            tag3: '2 A',
            statusGuru: ['pns'],
            status: 'Pegawai Negeri Sipil'

        },
        {
            imageUrl: 'user.png',
            namaGuru: 'Bambang Setiawan',
            nomorSk: 'SKI-MEI-012212',
            tahunAktif: '2011',
            tag1: 'Kelas 5 A',
            tag2: 'Kelas 4 B',
            tag3: 'Kelas 3 C',
            statusGuru: ['pns'],
            status: 'Pegawai Negeri Sipil'
        },
        {
            imageUrl: 'user.png',
            namaGuru: 'Triyanto',
            nomorSk: 'SKI-MEI-012212',
            tahunAktif: '2009',
            tag1: 'Kelas 5 A',
            tag2: 'Kelas 4 B',
            tag3: 'Kelas 3 C',
            statusGuru: ['pns'],
            status: 'Pegawai Negeri Sipil'
        },
        {
            imageUrl: 'user.png',
            namaGuru: 'Nunung Sutisna',
            nomorSk: 'SKI-MEI-012212',
            tahunAktif: '2010',
            tag1: 'Kelas 5 A',
            tag2: 'Kelas 4 B',
            tag3: 'Kelas 3 C',
            statusGuru: ['pns'],
            status: 'Pegawai Negeri Sipil'
        },
        {
            imageUrl: 'user.png',
            namaGuru: 'Reza Tanuwijaya',
            nomorSk: 'SKI-MEI-012212',
            tahunAktif: '2021',
            tag1: 'Kelas 5 A',
            tag2: 'Kelas 4 B',
            tag3: 'Kelas 3 C',
            statusGuru: ['honorer'],
            status: 'Pegawai Honorer'
        },
    ];

    const columns = [
        {
            title: 'Name',
            dataIndex: 'namaGuru',
            filters: [
                {
                    text: 'John',
                    value: 'John',
                },
                {
                    text: 'James',
                    value: 'James',
                },
            ],
            onFilter: (value, record) => record.namaGuru.indexOf(value) === 0,
            sorter: (a, b) => a.namaGuru.length - b.namaGuru.length,
            sortDirections: ['descend'],
        },
        {
            title: 'Nomor SK',
            dataIndex: 'nomorSk',
            defaultSortOrder: 'descend',
            // sorter: (a, b) => a.nomorSk - b.nomorSk,
        },
        {
            title: 'Status',
            dataIndex: 'statusGuru',
            render: statusGuru => (
                <>
                    {statusGuru.map(statusGuru => {
                        let color = statusGuru.length > 5 ? 'blue' : 'green';
                        return (
                            <Tag style={{borderRadius: '15px'}} color={color} key={statusGuru}>
                                {statusGuru.toUpperCase()}
                            </Tag>

                        );
                    })}
                </>
            ),
            filters: [
                {
                    text: 'Pegawai Negeri Sipil',
                    value: 'pns',
                },
                {
