import React, {Fragment, useState} from 'react';
import Adminsidebar from "../../components/Adminsidebar";
import AdminTopnav from "../../components/AdminTopnav";
import Adminfooter from "../../components/Adminfooter";
import {Button, Card, DatePicker, notification, PageHeader} from "antd";
import Upload from "antd/es/upload/Upload";
import {CheckOutlined, PlusOutlined, SaveOutlined} from "@ant-design/icons";
import ImgCrop from "antd-img-crop";

function ProfilSekolah() {
    const [fileList, setFileList] = useState([
        {
            uid: '-1',
            name: 'image.png',
            status: 'done',
            url: 'https://zos.alipayobjects.com/rmsportal/jkjgkEfvpUPVyRjUImniVslZfWPnJuuZ.png',
        },
    ]);
    const onChange = ({fileList: newFileList}) => {
        setFileList(newFileList);
    };

    const onPreview = async file => {
        let src = file.url;
        if (!src) {
            src = await new Promise(resolve => {
                const reader = new FileReader();
                reader.readAsDataURL(file.originFileObj);
                reader.onload = () => resolve(reader.result);
            });
        }
        const image = new Image();
        image.src = src;
        const imgWindow = window.open(src);
        imgWindow.document.write(image.outerHTML);
    };

    const _submitProfile = (e) => {
        e.preventDefault();
        console.log('submitted')
        notification.open({
            message: 'Success',
            description:
                'Profil Sekolah berhasil diubah',
            icon: <CheckOutlined style={{color: '#108ee9'}}/>,
            duration: 2
        });
    }

    return (
        <Fragment>
            <div id="wrapper">
                <Adminsidebar/>

                <div id="content-wrapper" className="d-flex flex-column">
                    <div id="content">
                        <AdminTopnav/>
                        <div className="container px-3 py-4 min-vh-100">
                            <div className="row">
                                <div className="col-lg-12">
                                    <PageHeader
                                        className="site-page-header card bg-lightblue text-grey-900 fw-700 "
                                        onBack={() => window.history.back()}
                                        title="Profil Sekolah"
                                    />
                                </div>
                                <div className="col-lg-12 mt-5">
                                    <div className="d-flex justify-content-center">
                                        <Card className="bg-lightblue" style={{width: 157}}>
                                            <ImgCrop rotate>
                                                <Upload
                                                    className="avatar-uploader"
                                                    action="https://www.mocky.io/v2/5cc8019d300000980a055e76"
                                                    listType="picture-card"
                                                    fileList={fileList}
                                                    onChange={onChange}
                                                    onPreview={onPreview}
                                                >
                                                    {fileList.length < 1 && <PlusOutlined/>}
                                                </Upload>
                                            </ImgCrop>
                                        </Card>
                                    </div>
                                    <form action="#">
                                        <div className="row">
                                            <div className="col-lg-6 mb-3">
                                                <div className="form-group">
                                                    <label className="mont-font fw-600 font-xsss">
                                                        Facebook
                                                    </label>
                                                    <input
                                                        type="text"
                                                        name="comment-name"
                                                        className="form-control"
                                                    />
                                                </div>
                                            </div>

                                            <div className="col-lg-6 mb-3">
                                                <div className="form-group">
                                                    <label className="mont-font fw-600 font-xsss">
                                                        Twitter
                                                    </label>
                                                    <input
                                                        type="text"
                                                        name="comment-name"
                                                        className="form-control"
                                                    />
                                                </div>
                                            </div>
                                        </div>
                                    </form>
                                </div>
                            </div>
                        </div>

                        <Adminfooter/>
                    </div>
                </div>
            </div>
        </Fragment>
    );
}

export default ProfilSekolah;
